section.script:
function addEngine()
{
  if((typeof window.sidebar == "object") && (typeof window.sidebar.addSearchEngine == "function"))
  {
    window.sidebar.addSearchEngine(
    "%MCR.baseurl%docportal-plugin.src",
    "%MCR.baseurl%docportal-plugin.png",
    "DocPortal", "web" );
  }
  else
  {
    alert("Netscape 6, Seamonkey, Firefox oder Mozilla ist für dieses Plug-in erforderlich!");
  }
}

